from typing import TextIOfrom config import OUTPUT_DIRfrom src.models.article import Articlefrom src.utils.data_extractor import DataExtractorclass ExtractionHandler:    current_article: Article    filename: str    current_file: TextIO    extractor: DataExtractor    def __init__(self):        self.extractor = DataExtractor("")    def extract_data_and_save_json_file(self):        self._extract_data_and_populate_article()        self._save_article_to_json_file()    def _extract_data_and_populate_article(self):        _file_red = self.current_file.read()        self.extractor.file_red = _file_red        self.current_article = Article.from_dict(self.extractor.extract_data())    def _save_article_to_json_file(self):        json_content = self.current_article.to_json()        self.filename = self.filename+".json"        with open(OUTPUT_DIR / self.filename, 'w', encoding='utf-8') as file:            file.write(json_content)